mapper.select:



select     a.id, a.industry_id, a.proposal_name, a.customer_id, a.customer_name, a.desc, a.purpose_id,a.purpose_col1,a.purpose_col2,
a.purpose_col3, a.version, a.theme_id,a.pic_id,b.url
from h_advertising_proposal a left join h_pic_video b on a.pic_id = b.id

  a.customer_name like #{pattern}
  and a.proposal_name like #{pattern1}
  and a.version like #{pattern2} 
  and a.industry_id = #{industryId,jdbcType=INTEGER}
 order by a.id desc




select     count(id)
from h_advertising_proposal

  customer_name like #{pattern}
  and proposal_name like #{pattern1}
  and version like #{pattern2} 
  and industry_id = #{industryId,jdbcType=INTEGER}
