mapper.select:

select a.id,a.title,a.content,a.module_id,a.status,a.proposal_id
from h_home a

   a.title like #{pattern}
   and a.status =  #{status,jdbcType=INTEGER}
  and a.proposal_id =  #{proposalId,jdbcType=INTEGER}
 order by a.id desc


select count(a.id)
from h_home a

   a.title like #{pattern}
   and a.status =  #{status,jdbcType=INTEGER}
  and a.proposal_id =  #{proposalId,jdbcType=INTEGER}
