mapper.select:

select a.id,a.media_name,a.summary,a.mobile,a.email,a.pic_id,a.import_level,b.url
from h_media a left join h_pic_video b on a.pic_id = b.id

  a.media_name like #{pattern}
  and a.mobile = #{mobile,jdbcType=VARCHAR}
   and a.import_level = #{importLevel,jdbcType=VARCHAR} and a.status = 1
 order by a.id desc


select count(id)
from h_media

  media_name like #{pattern}
  and mobile = #{mobile,jdbcType=VARCHAR}
   and import_level = #{importLevel,jdbcType=VARCHAR} and status =1
