mapper.select:
select
a.id, a.name, a.module_id, a.method_type, a.media_id, a.industry_id, a.price,a.light_exposure,a.pv, a.import_level, a.desc,
a.characteristic, a.status
from h_method a
 join h_tag_method b on a.id = b.method_id

    a.media_id = #{mediaId,jdbcType=INTEGER}
    and a.industry_id = #{industryId,jdbcType=INTEGER}
     and a.import_level = #{importLevel,jdbcType=VARCHAR}
     and b.tag_id in

      #{tagId}


    and a.status = 1
   order by a.id desc

select count(a.id) from
h_method a
 join h_tag_method b on a.id = b.method_id

  a.media_id = #{mediaId,jdbcType=INTEGER}
  and a.industry_id = #{industryId,jdbcType=INTEGER}
   and a.import_level = #{importLevel,jdbcType=VARCHAR}
   and b.tag_id in

      #{tagId}


  and a.status = 1
