mapper.select:

select a.id,a.username,a.status  from s_user a

  username like #{pattern}
  and c.role_id = #{roleId,jdbcType=INTEGER} 
  and a.status = #{status,jdbcType=INTEGER}
 order by id desc


select count(distinct id) from s_user

  username like #{pattern}
  and c.role_id = #{roleId,jdbcType=INTEGER} 
  and a.status = #{status,jdbcType=INTEGER}
